import React, { useState } from "react";
import {
  FoodBox,
  FoodBoxText,
  FoodButtonsDiv,
  FoodPhoto,
  TextSerif,
} from "../styles/MainScreen";
import { AddSubButt } from "../styles/Buttons";
import { Text } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { colors } from "../locales/colors";

export const FoodTile = (data: Food, cart: Food[], setCart: any) => {
  const [amount, setAmount] = useState(1);

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  function cutText(text: string, length: number) {
    return text.substr(0, length);
  }

  return (
    <FoodBox key={data.id.toString()}>
      <FoodPhoto theme={data.imageUrl}>
        <FoodButtonsDiv>
          <FoodPhotoButton icon={"info"} color={colors.buttonColor1} />
          <FoodPhotoButton
            icon={"cart"}
            color={colors.buttonColor1}
            onClick={() => add()}
          />
        </FoodButtonsDiv>
      </FoodPhoto>
      <FoodBoxText>
        {cutText("asda sdasd asd asd das sad as as asd", 22)}
      </FoodBoxText>
      <div
        style={{
          flexDirection: "row",
          display: "flex",
        }}
      >
        <TextSerif>{data.price}$</TextSerif>
        <FoodPhotoButton
          icon={"minus"}
          color={colors.buttonColor1}
          sqr={true}
          onClick={() => (amount > 1 ? setAmount(amount - 1) : null)}
        />
        <TextSerif>{amount}</TextSerif>
        <FoodPhotoButton
          icon={"plus"}
          color={colors.buttonColor1}
          sqr={true}
          onClick={() => setAmount(amount + 1)}
        />
      </div>
      {/* <Text>{data.name}</Text> */}
    </FoodBox>
  );
};
