import { createSlice } from "@reduxjs/toolkit";
import { Food } from "../models/Food";
import { FoodData } from "../testData/FoodData";

export interface CartI {
  cart: Food[];
}

const initialState: CartI = {
  cart: [],
};
export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    clear: (state) => {
      state.cart = initialState.cart;
    },
    upgradeCart: (state, action) => {
      let clone = [...state.cart];
      let count = clone.filter(
        (data: any) => data.id === clone[clone.length - 1].id
      );
      let qqq = clone.findIndex(
        (data1: any) => data1.id === clone[clone.length - 1].id
      );
      if (count.length >= 2) {
        console.log("xxxxxxxxxxxx", clone[qqq]);
        // clone[qqq].amount =
        //   clone[qqq].amount + clone[action.payload.length - 1].amount;
        state.cart = [...clone];
      } else state.cart = [...state.cart, action.payload];
    },
    upgradeCartAdd: (state, action) => {
      state.cart[action.payload].amount += 1;
    },
    upgradeCartDesc: (state, action) => {
      state.cart[action.payload].amount -= 1;
    },
    upgradeCartDelete: (state, action) => {
      state.cart = [action.payload];
    },
  },
});

export const {
  upgradeCart,
  clear,
  upgradeCartAdd,
  upgradeCartDesc,
  upgradeCartDelete,
} = cartSlice.actions;

export default cartSlice.reducer;
