import styled from "styled-components";
import { colors, rawColors } from "../locales/colors";

export const HeaderBox = styled.div`
  background-color: ${colors.bg};
  height: 50px;
  position: fixed;
  width: 100%;
  top: 0;
  display: flex;
  justify-content: center;
  z-index: 99;
`;

export const TitleTxt = styled.div`
  flex: 1;
  background-color: ${colors.bg};
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const LoginBox = styled.div`
  flex: 1;
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SearchMain = styled.div`
  z-index: 2;
  background-color: ${colors.buttonIconAct};
  max-width: 35%;
  min-width: 400px;
  height: 50px;

  position: absolute;
  display: flex;
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
  &:hover {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
    box-shadow: 0 4px 8px 0;
  }
  &:not(:hover) {
    position: fixed;
    transform: translate(0%, 0%);
    transition: 0.3s ease-out;
  }
`;

export const CircleBox = styled.div`
  background-color: ${colors.bg};
  border-radius: 0 0 100px 100px;
  position: absolute;
  width: 15%;
  height: 30px;
  margin: 50px 42.5%;
  z-index: 1;
  box-shadow: 0 4px 10px 0;
  display: flex;
  justify-content: center;
  alignt-items: center;
`;

export const SerachInput = styled.input`
  color: ${rawColors.red};
`;

export const SearchBarBox = styled.div`
  flex: 2;
  justify-content: center;
  align-items: center;
  display: flex;
`;

export const SearchFiltersBox = styled.div`
  flex: 1;
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;
`;

export const LogoBox = styled.div`
  flex: 1;
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const FilterButton = styled.button`
  background-color: ${(props) => props.theme};
  min-width: 100px;
  &:hover {
    background-color: ${colors.filterBtnHover};
  }
`;
