import { useState } from "react";
import { paymentMethods } from "../styles/Constants";
import {
  InputBaseBox,
  InputButtonBox,
  InputChoose,
  OptionBox,
} from "../styles/Input";
import { ErrorInputText } from "../styles/TextStyles";
import { countTime } from "./functions/countTime";

export const InputPopup = ({
  data,
  register,
  valueTemp,
  setOpenPopup,
}: any) => {
  return (
    <InputChoose
      type="submit"
      theme={{ width: "100%" }}
      placeholder={data?.placeholder}
      defaultValue={data?.defaultValue}
      {...register(data.value)}
      {...(register(), { required: data.required })}
    >
      {data.value === "deliveryTime"
        ? countTime().map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))
        : paymentMethods.map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))}
    </InputChoose>
  );
};

export const InputBase = ({ data, register, errors }: any) => {
  return (
    <>
      <InputBaseBox
        theme={{ width: "100%", height: "80%" }}
        placeholder={data?.placeholder}
        {...register(data.value)}
        {...(register(), { required: data.required })}
      />
      {errors.exampleRequired && <span>This field is required</span>}
    </>
  );
};

export const InputButton = ({
  data,
  register,
  valueTemp,
  setOpenPopup,
  getValues,
}: any) => {
  return (
    <InputButtonBox
      type={"button"}
      theme={{ width: "100%" }}
      value={
        getValues()[data.value] ? getValues()[data.value] : data.defaultValue
      }
      onClick={() => setOpenPopup({ isActive: true, variant: data.value })}
    />
  );
};
