import { InputsI } from "../models/Inputs";

export const margin = {
  xs: "2px",
  s: "5px",
  m: "10px",
  ml: "20px",
  l: "30px",
  xl: "45px",
  xxl: "50px",
  xsPrc: "2%",
  sPrc: "5%",
  mPrc: "10%",
  xlPrc: "45%",
};

export const CartBarWidthReq = 300;

export const Constants = {
  size: {
    foodPhotoSize: "200px",
  },

  borderRadius: {},
  font: {
    xs: "12px",
    s: "14px",
    m: "20px",
    l: "28px",
    xxl: "37px",
  },
  forms: {
    deliveryData: "Delivery data",
    streetName: "Street Name",
    streetNameDesc: "Type street name",
    city: "City",
    cityDesc: "Type your city",
    houseNumber: "House number",
    houseNumberDesc: "Type house number",
    flatNumber: "Flat number (optional)",
    flatNumberDecs: "Type flatnumber",
    postcode: "Postcode",
    postCodeDesc: "Type postalcode",
    accessCode: "Access code (optional)",
    accessCodeDesc: "Type acces code",
    floor: "Floor (optional)",
    floorDesc: "Type floor numer",
    companyName: "Company name (optional)",
    companyNameDesc: "Type comany name",
    addNote: "Add note (optional)",
    addNoteDesc: "i.e. PLease on't ring the bell",
    personalData: "Personal data",
    firstLastName: "First and last name",
    firstLastNameDesc: "Type your first and last name",
    phoneNumber: "Phone number",
    phoneNumberDesc: "i.e. +48 xxx xxx xx",
    email: "E-mail",
    emailDesc: "example@email.com",
    deliveryTime: "Delivery time",
    payWith: "Pay with",
    voutcher: "Vouther (optional)",
    voutcherDesc: "Type your voutcher",
  },
};

export const inputDataDelivery: InputsI = {
  data: [
    {
      title: Constants.forms.streetName,
      placeholder: Constants.forms.streetNameDesc,
      value: "streetName",
      type: "text",
      required: true,
    },
    {
      title: Constants.forms.city,
      placeholder: Constants.forms.cityDesc,
      value: "city",
      type: "text",
      required: true,
    },
    {
      title: Constants.forms.houseNumber,
      placeholder: Constants.forms.houseNumberDesc,
      value: "houseNumber",
      type: "text",
      required: true,
    },
    {
      title: Constants.forms.flatNumber,
      placeholder: Constants.forms.flatNumberDecs,
      value: "flatNumber",
      type: "text",
      required: false,
    },
    {
      title: Constants.forms.postcode,
      placeholder: Constants.forms.postCodeDesc,
      value: "postcode",
      type: "text",
      required: true,
    },
    {
      title: Constants.forms.accessCode,
      placeholder: Constants.forms.accessCodeDesc,
      value: "accessCode",
      type: "text",
      required: false,
    },
    {
      title: Constants.forms.floor,
      placeholder: Constants.forms.floorDesc,
      value: "floor",
      type: "text",
      required: false,
    },
    {
      title: Constants.forms.companyName,
      placeholder: Constants.forms.companyNameDesc,
      value: "companyName",
      type: "text",
      required: false,
    },
    {
      title: Constants.forms.addNote,
      placeholder: Constants.forms.addNoteDesc,
      value: "note",
      type: "text",
      required: false,
    },
  ],
};

export const inputDataPersonal = {
  data: [
    {
      title: Constants.forms.firstLastName,
      placeholder: Constants.forms.firstLastNameDesc,
      value: "firstLastName",
      type: "text",
      required: true,
    },
    {
      title: Constants.forms.phoneNumber,
      placeholder: Constants.forms.phoneNumberDesc,
      value: "phoneNumber",
      type: "text",
      required: true,
    },
    {
      title: Constants.forms.email,
      placeholder: Constants.forms.emailDesc,
      value: "email",
      type: "text",
      required: true,
      typeOfData: "email",
    },
  ],
};

export const inputDataSpecial = {
  data: [
    {
      title: Constants.forms.deliveryTime,
      value: "deliveryTime",
      type: "button",
      required: true,
      defaultValue: "As soon as possible",
    },
    {
      title: Constants.forms.payWith,
      value: "payWith",
      type: "button",
      required: true,
      defaultValue: "Choose a method",
    },
    {
      title: Constants.forms.voutcher,
      placeholder: Constants.forms.voutcherDesc,
      value: "voutcher",
      type: "text",
      required: false,
    },
  ],
};

export const inputDataSpecialPopup = {
  data: [
    {
      title: Constants.forms.deliveryTime,
      value: "deliveryTime",
      type: "popup",
      required: true,
      defaultValue: "As soon as possible",
    },
    {
      title: Constants.forms.payWith,
      value: "payWith",
      type: "popup",
      required: true,
      defaultValue: "Choose a method",
    },
  ],
};

export const paymentMethods = ["Card", "PayPal", "PayU", "On delivery"];
