import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { FooterBox } from "../style";
import { SerifText, SerifTextS } from "../styles/TextStyles";
import { getWindowSize } from "./functions/getWindowSize";
import { moveCartSlider } from "./functions/moveCartSlider";

export const Footer = () => {
  const [windowSize, setWindowSize] = useState(getWindowSize());
  const cart = useSelector((state: any) => state.cart.cart);

  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }
    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  return (
    <FooterBox theme={{ width: windowSize.innerWidth }}>
      {/* <SerifText>Join us! Gain some sweet voutchers for free!</SerifText> */}
      <SerifTextS>p.wolf@gmail.com</SerifTextS>
      <SerifTextS>+48 123 456 789</SerifTextS>
      <SerifTextS>ul. Wymyślona 6, 59-111 Wrocław</SerifTextS>
      <SerifTextS>@P.Wolf</SerifTextS>
    </FooterBox>
  );
};
