import { useForm } from "react-hook-form";
import {
  Constants,
  inputDataDelivery,
  inputDataPersonal,
  inputDataSpecial,
} from "../../styles/Constants";
import { setInputs } from "../functions/setInputsToForm";
import { GridBox2 } from "../../style";
import { FormBox, FormSpecialBox } from "../../styles/OrderTrans";
import { GrayButtonInput } from "../../styles/Input";
import { InputSectionText } from "../../styles/TextStyles";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { setForm } from "../../store/form";

export const FormOrderDetails = () => {
  const {
    register,
    setValue,
    handleSubmit,
    getValues,
    formState: { errors },
  } = useForm();
  const dispatch = useDispatch();
  const onSubmit = (data: any) => console.log(getValues());
  useEffect(() => {
    const interval = setInterval(() => {
      dispatch(setForm(getValues()));
    }, 10000);
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <GridBox2 onSubmit={handleSubmit(onSubmit)}>
        <InputSectionText>{Constants.forms.deliveryData}</InputSectionText>
        <FormBox>
          {setInputs({ data: inputDataDelivery.data, register, errors })}
        </FormBox>
        <InputSectionText>{Constants.forms.deliveryTime}</InputSectionText>
        <FormBox>
          {setInputs({ data: inputDataPersonal.data, register, errors })}
        </FormBox>
        <FormSpecialBox>
          {setInputs({
            data: inputDataSpecial.data,
            register,
            getValues,
            errors,
            setValue,
          })}
        </FormSpecialBox>
        <GrayButtonInput type="submit" />
      </GridBox2>
    </>
  );
};
