import { generateKey } from "crypto";
import { useState, useEffect } from "react";
import { Filter } from "../../models/Filter";
import { Food } from "../../models/Food";
import { FoodTile } from "../../models/FoodComp";
import { CardSlider, CartBox, GridBox, Mainbox } from "../../style";
import { FoodData } from "../../testData/FoodData";
import { CartSliderBox } from "../CartSliderBox";
import { sortFoodBy } from "../functions/sortFoodBy";

export const Main = (filter: Filter) => {
  const [food, setFood] = useState<Food[]>(FoodData);
  const [cart, setCart] = useState<Food[]>([]);

  useEffect(() => {
    move();
    generate();

    return () => {};
  }, [cart]);

  function generate() {
    return <CartSliderBox cart={cart} setCart={setCart}></CartSliderBox>;
  }

  function move() {
    let e = document.getElementById("CardSlider");

    if (e && cart.length !== 0) {
      e.style.transform = "translate(-100%)";
      e.style.transition = "0.6s ease-out";
    } else if (e) {
      e.style.transform = "translate(0%)";
      e.style.transition = "0.6s ease-out";
    }
  }

  console.log(filter);

  return (
    <Mainbox>
      <GridBox>{sortFoodBy(food)}</GridBox>
      <CartBox>{generate()}</CartBox>
    </Mainbox>
  );
};
