import { useState } from "react";
import { SearchFiltersBox } from "../style";

export const Filters = () => {
  let list = [
    { name: "Name A-Z" },
    { name: "Name Z-A', Price from low to high" },
    { name: "Price from high to low" },
    { name: "Amount from low do high" },
    { name: "Amount from high to low" },
  ];

  const [showList, setShowList] = useState(false);
  return (
    <SearchFiltersBox>
      <button onClick={() => setShowList(!showList)}></button>
      {!showList ? <></> : list.map((x) => <button>{x.name}</button>)}
    </SearchFiltersBox>
  );
};
