import { createSlice } from "@reduxjs/toolkit";
import { Food } from "../models/Food";
import { FoodData } from "../testData/FoodData";

export interface FoodI {
  food: Food[];
}

const initialState: FoodI = {
  food: FoodData,
};
export const foodSlice = createSlice({
  name: "food",
  initialState,
  reducers: {
    incrementAmount: (state, action) => {
      state.food.find((e) => e.id == 6)
        ? (state.food.find((e) => e.id == 6).amount += 1)
        : null;
    },
    decrementAmount: (state, action) => {
      state.food[action.payload].amount -= 1;
    },
    clear: (state, action) => {
      state.food[action.payload].amount = 1;
    },
  },
});

export const { incrementAmount, decrementAmount, clear } = foodSlice.actions;

export default foodSlice.reducer;
