import { Food } from "../../models/Food";

export function sortFoodByAlph(food: Food[], type: boolean) {}

export function sortFoodByOther(food: Food[], type: string, inOrder: boolean) {
  switch (type) {
    case "amount":
      return food.sort(function (a, b) {
        return inOrder ? b.amount + a.amount : b.amount - a.amount;
      });
    case "price":
      return food.sort(function (a, b) {
        return inOrder ? b.price + a.price : b.price - a.price;
      });
    case "name":
      return food.sort(function (a, b) {
        return a.name.localeCompare(b.name);
      });
    default:
      return food;
  }
}
