import { Food } from "../../models/Food";

export function sortFoodByAlph(food: Food[], type: boolean) {
  return food.sort(function (a, b) {
    if (type) return a.name.localeCompare(b.name);
    else return b.name.localeCompare(a.name);
  });
}

export function sortFoodByOther(food: Food[], type: string, inOrder: boolean) {
  switch (type) {
    case "amount":
      return food.sort(function (a, b) {
        return a.amount + b.amount;
      });
    case "price":
      return food.sort(function (a, b) {
        console.log(a.price, b.price);
        return inOrder ? a.price + b.price : b.price + a.price;
      });
    default:
      return "suck";
  }
}
