import { createSlice } from "@reduxjs/toolkit";
import { Food } from "../models/Food";
import { FoodData } from "../testData/FoodData";

export interface CartI {
  cart: Food[];
}

const initialState: CartI = {
  cart: [],
};
export const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    clear: (state) => {
      state.cart = initialState.cart;
    },
    upgradeCart: (state, action) => {
      let count = state.cart.filter(
        (data: any) => data.id === state.cart[state.cart.length - 1].id
      );
      let qqq = state.cart.findIndex(
        (data1: any) => data1.id === state.cart[state.cart.length - 1].id
      );
      console.log("check", count, qqq);
      if (count.length >= 2) {
        newCart.push(clone[qqq]);
        cart.pop();
      }
      state.cart = [
        ...(state.cart[qqq].amount + state.cart[state.cart.length - 1].amount),
      ];
    },
    upgradeCartAdd: (state, action) => {
      state.cart[action.payload].amount += 1;
    },
    upgradeCartDesc: (state, action) => {
      state.cart[action.payload].amount -= 1;
    },
    upgradeCartDelete: (state, action) => {
      state.cart = [action.payload];
    },
  },
});

export const {
  upgradeCart,
  clear,
  upgradeCartAdd,
  upgradeCartDesc,
  upgradeCartDelete,
} = cartSlice.actions;

export default cartSlice.reducer;
