import { TxtButton } from "../../styles/Buttons";
import { LinkTextBox } from "../../styles/TextStyles";

export function chooseAmountOfProducts(
  setNumberOnSite: any,
  numberOnSite: number
) {
  function setNumber(number: number) {
    setNumberOnSite(number);
  }

  function textButton(x: number) {
    return (
      <TxtButton
        onClick={() => setNumber(x)}
        theme={numberOnSite === x ? true : false}
      >
        {x}
      </TxtButton>
    );
  }

  return (
    <LinkTextBox>
      {textButton(30)}
      {textButton(60)}
      {textButton(90)}
    </LinkTextBox>
  );
}
