import styled from "styled-components";
import { rawColors, colors } from "../locales/colors";
import { Constants } from "./Constants";

export const SearchMainBox = styled.div`
  z-index: 1;
  background-color: ${colors.bg};
  min-width: 400px;
  height: 60px;
  top: -10px;
  position: absolute;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
  ${(props) =>
    props.theme === true
      ? `  {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
    box-shadow: 0 4px 8px 0;
  }`
      : `  {
        position: fixed;
        transform: translate(0%, 0%);
        transition: 0.3s ease-out;
      }`};
`;

export const SearchHandleBox = styled.div`
  background-color: ${colors.bg};
  border-radius: 0 0 100px 100px;
  position: absolute;
  width: 50px;
  height: 50px;
  margin: 60px;
  box-shadow: 0 4px 8px 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const SearchHiddenHandleBox = styled.div`
  width: 50px;
  height: ${(props) => (props.theme === true ? `300px` : `100px`)};
  position: absolute;
  opacity: 0.5;
`;

export const SerachInput = styled.input`
  color: ${rawColors.red};
  width: 90%;
  display: flex;
`;

export const SearchBarBox = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const SearchFiltersBox = styled.div`
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
  z-index: 999;
  position: absolute;
`;
