import { flexbox } from "@mui/system";
import { GrayButton } from "../styles/Buttons";
import { inputDataSpecial, inputDataSpecialPopup } from "../styles/Constants";
import {
  PopupWindowMainBox,
  PopupWindowBox,
  GrayButtonInput,
} from "../styles/Input";
import { FormSpecialBox } from "../styles/OrderTrans";
import { setSingleInput, setInputs } from "./functions/setInputsToForm";

export function popupBlock(options: any, handle: any, register: any) {
  let isOpen = options.isActive;
  if (isOpen === true) {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "hidden";
    }
    return (
      <PopupWindowMainBox>
        <PopupWindowBox style={{}}>
          <form onSubmit={handle}>
            {setSingleInput(inputDataSpecialPopup.data[0], register, "")}
          </form>
          <GrayButton onClick={() => isOpen === false} />
        </PopupWindowBox>
      </PopupWindowMainBox>
    );
  } else {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "auto";
    }
  }
}
