import { createSlice } from "@reduxjs/toolkit";

export interface FormI {
  data: {
    accessCode: string;
    city: string;
    companyName: string;
    deliveryTime: string;
    firstLastName: string;
    floor: string;
    email: string;
    flatNumber: string;
    houseNumber: string;
    payWith: string;
    phoneNumber: string;
    postcode: string;
    streetName: string;
    note: string;
    voutcher: string;
  };
}

const initialState: FormI = {
  data: {
    accessCode: "",
    city: "",
    companyName: "",
    deliveryTime: "",
    firstLastName: "",
    floor: "",
    email: "",
    flatNumber: "",
    houseNumber: "",
    payWith: "",
    phoneNumber: "",
    postcode: "",
    streetName: "",
    note: "",
    voutcher: "",
  },
};
export const formSlice = createSlice({
  name: "form",
  initialState,
  reducers: {
    setForm: (state, action) => {
      let koko: any = action.payload?.name;
      state.data[koko] = action.payload;
    },
    clearForm: (state) => {
      state.data = initialState.data;
    },
  },
});

export const { clearForm, setForm } = formSlice.actions;

export default formSlice.reducer;
