import { useState } from "react";
import { DeleteImg } from "../locales/images/delete";
import { SearchImg } from "../locales/images/search";
import { StartSearchImg } from "../locales/images/startSearch";
import { Filter } from "../models/Filter";
import { IconButton } from "../styles/Buttons";
import { InfoScreenTextBox } from "../styles/MainScreen";
import { SearchBarBox, SearchHandleBox, SearchMainBox } from "../styles/Search";
import { Filters } from "./Filters";
import { SearchForm } from "./forms/SearchForm";
export const Search = ({ filter, setFilter }: any) => {
  const [isHidden, setIsHidden] = useState(false);
  return (
    <SearchMainBox theme={isHidden}>
      <div style={{ backgroundColor: "red", width: "100%", height: "100%" }}>
        <SearchBarBox>
          <SearchForm />

          <StartSearchImg />
          <Filters filter={filter} setFilter={setFilter}></Filters>
        </SearchBarBox>
      </div>
      <SearchHandleBox onClick={() => setIsHidden(!isHidden)}>
        {isHidden ? <SearchImg /> : <SearchImg />}
      </SearchHandleBox>
    </SearchMainBox>
  );
};
