import { useState, useEffect } from "react";
import { LogoImg } from "../../locales/images/logo";
import { Food } from "../../models/Food";
import { FoodTile } from "../../models/FoodComp";
import { BgLogo, GridBox } from "../../style";
import { CartBox } from "../../styles/CartBox";
import { FoodData } from "../../testData/FoodData";
import { CartSlider } from "../CartSlider";
import { moveCartSlider } from "../functions/moveCartSlider";
import { sortFoodBy } from "../functions/sortFoodBy";

export const HomeSub = ({ filter }: any) => {
  const [food, setFood] = useState<Food[]>(FoodData);
  const [cart, setCart] = useState<Food[]>([]);
  const [windowSize, setWindowSize] = useState(getWindowSize());

  function getWindowSize() {
    const { innerWidth, innerHeight } = window;
    return { innerWidth, innerHeight };
  }
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  return (
    <>
      <GridBox>{food.map((data, index) => FoodTile(data, setCart))}</GridBox>
      <CartBox
        theme={{
          img: require("../../locales/images/foodBg1.png"),
          width: windowSize.innerWidth,
        }}
      >
        <CartSlider cart={cart} setCart={setCart} />
      </CartBox>
    </>
  );
};
