import { paymentMethods } from "../styles/Constants";
import {
  InputBaseBox,
  InputButtonBox,
  InputChoose,
  OptionBox,
} from "../styles/Input";
import { countTime } from "./functions/countTime";
import { useDispatch } from "react-redux";
import { setPopup } from "../store/popup";
import { setForm } from "../store/form";
export const InputPopup = ({ data, register, other }: any) => {
  const dispatch = useDispatch();
  return (
    <InputChoose
      type="submit"
      theme={{ width: "100%" }}
      placeholder={data?.placeholder}
      defaultValue={data?.defaultValue}
      required={data.required}
      pattern={data.pattern}
      onChange={() => console.log("sikkkke")}
      {...register(data.value)}
    >
      <Controller
        control={control}
        name="folderSelect"
        onChange={() => console.log("hellow")}
        defaultValue=""
        render={({ onChange, value, onBlur, name }) => (
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            onChange={onChange}
            value={value ? value : ""}
            name={name}
          >
            <MenuItem value="Invoices" key="Invoices">
              Invoices
            </MenuItem>
            <MenuItem value="Statements" key="Statements">
              Statements
            </MenuItem>
            <MenuItem value="Credits" key="Credits">
              Credits
            </MenuItem>
          </Select>
        )}
      />
      {data.value === "deliveryTime"
        ? countTime().map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))
        : paymentMethods.map((item: any) => (
            <OptionBox value={item}>{item}</OptionBox>
          ))}
    </InputChoose>
  );
};

export const InputBase = ({ data, register, errors }: any) => {
  return (
    <>
      <InputBaseBox
        theme={{ width: "100%" }}
        type={data.typeOfData}
        placeholder={data?.placeholder}
        required={data.required}
        pattern={data.pattern}
        {...register(data.value)}
      />
    </>
  );
};

export const InputButton = ({ data, register, getValues, setValue }: any) => {
  const dispatch = useDispatch();
  console.log(getValues("deliveryTime"));
  return (
    <InputButtonBox
      type={"button"}
      theme={{ width: "100%" }}
      onClick={() =>
        dispatch(
          setPopup({
            message: "pickListName",
            type: data.value,
            onConfirm: "AddMyList",
            register: register,
            other: setValue,
          })
        )
      }
    >
      {getValues("deliveryTime")}
    </InputButtonBox>
  );
};

