import styled from "styled-components";
import { rawColors, colors } from "./locales/colors";

export const FooterBox = styled.div`
  background-color: ${rawColors.pink};
  height: 50px;
  position: relative;
  position: relative;
`;

export const Mainbox = styled.div`
  background-color: ${colors.bg};
  min-height: 100%;
  margin-top: 50px;
  display: flex;
`;

export const AppBox = styled.div`
  height: 100%;
  width: 100%;
  background-color: ${colors.bg};
`;

export const FoodBox = styled.div`
  max-height: 400px;
  min-height: 200px;
  min-width: 200px;
  max-width: 400px;
  border: 2px solid red;
  border-radius: 30px;
  margin: 10px;
  display: flex;
  flex-direction: column;
`;

export const GridBox = styled.div`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

export const CartBox = styled.div`
  min-height: 100%;
  width: 20%;
  overflow: hidden;
  z-index: 0;
  background-image: url(${(props) => props.theme});
  background-repeat: no-repeat;
  background-size: 100% 100%;
`;

export const CardSlider = styled.div`
  flex: 1;
  width: 100%;
  height: 100%;
  background-color: ${rawColors.orange};
  opacity: 0.9;
  margin-left: 100%;
  z-index: -4;

  &:hover {
    transform: translate(${(props) => props.theme}, 0);
    transition: 1s ease-out;
  }
`;

export const FoodPhoto = styled.img`
  height: 220px;
  width: 220px;
  border-radius: 28px 28px 0px 0px;
`;

export const SearchMain = styled.div`
  background-color: ${rawColors.blue};
  max-width: 35%;
  min-width: 400px;
  height: 50px;
  position: absolute;
  display: flex;
  z-index: 2;
  &:hover {
    position: fixed;
    transform: translate(0%, 100%);
    transition: 0.3s ease-out;
  }
  &:not(:hover) {
    position: fixed;
    transform: translate(0%, 0%);
    transition: 0.3s ease-out;
  }
  border-bottom-right-radius: 30%;
  border-bottom-left-radius: 30%;
`;

export const HeaderBox = styled.div`
  background-color: ${colors.bg};
  height: 50px;
  position: fixed;
  width: 100%;
  top: 0;
  display: flex;
  justify-content: center;
  z-index: 99;
`;

export const Logo = styled.div`
  flex: 1;
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Title = styled.div`
  flex: 1;
  background-color: ${colors.bg};
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Login = styled.div`
  flex: 1;
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const CircleBox = styled.div`
  border-radius: 100%;
  position: absolute;
  width: 50px;
  height: 50px;
  background-color: ${rawColors.blue};
  margin: 20px 0 0 40%;
  z-index: 1;
`;

export const SerachInput = styled.input`
  color: ${rawColors.red};
`;

export const SearchBarBox = styled.div`
  flex: 2;
  justify-content: center;
  align-items: center;
  display: flex;
  z-index: 2;
`;

export const SearchFiltersBox = styled.div`
  flex: 1;
  justify-content: center;
  display: flex;
  flex-wrap: wrap;
  margin-top: 10px;
`;

export const CartFoodElement = styled.div`
  height: 50px;
  width: 90%;
  margin: 5%;
  margin-top: 0%;
  display: flex;
  flex: 1;
`;

export const SmallImg = styled.img`
  width: 30px;
  height: 30px;
  align-self: center;
  justify-content: center;
  display: flex;
`;

export const FlexPart = styled.div`
  display: flex;
  flex: ${(props) => props.theme};
  align-items: center;
  justify-content: center;
`;

export const ButtonPhoto = styled.button`
  width: 30px;
  height: 30px;
  background-color: ${(props) =>
    props.theme.isActive === false ? colors.buttonColor1 : colors.buttonbg};
  border-radius: ${(props) => (props.theme.sqr ? "0px" : "10000px")};
  border: 2px solid ${colors.buttonBorder};
  display: flex;
  justify-content: center;
  align-items: center;
  // &:hover {
  //   transition: opacity 0.3s;
  //   opacity: 0;
  // }
  // &:not(:hover) {,
  //   transition: opacity 0.3s;
  //   opacity: 1;
