export const InputBase = ({ data, register, errors }: any) => {
  return (
    <>
      <InputBaseBox
        theme={{ width: "100%" }}
        type={data.typeOfData}
        placeholder={data?.placeholder}
        required={data.required}
        pattern={data.pattern}
        {...register(data.value)}
      />
    </>
  );
};

export const InputButton = ({ data, register, getValues, setValue }: any) => {
  const dispatch = useDispatch();

  return (
    <InputButtonBox
      type={"button"}
      theme={{ width: "100%" }}
      onClick={() =>
        dispatch(
          setPopup({
            message: "pickListName",
            type: data.value,
            onConfirm: "AddMyList",
            register: register,
            other: setValue,
          })
        )
      }
    >
      xD
    </InputButtonBox>
  );
};

