import { useEffect, useState } from "react";
import {
  FoodBox,
  FoodBoxAmountCont,
  FoodBoxRestConst,
  FoodBoxTitleCont,
  FoodButtonsDiv,
  FoodPhoto,
} from "../styles/MainScreen";
import { FoodBoxText, OswaldText, SerifText } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { colors } from "../locales/colors";
import { cutText } from "../components/functions/cutText";
import { BlockImg } from "../locales/images/block";
import { showProductInfo } from "../components/functions/showProductInfo";
import { InfoScreen } from "../components/InfoScreen";
import { Constants } from "../styles/Constants";
import { pl } from "../locales/languages/pl";
import { getString } from "../locales/languages/string";
import { useDispatch } from "react-redux";
import { decrementAmount, incrementAmount, clear } from "../store/food";

export const FoodTile = (
  data: Food,
  setCart: any,
  index: number,
  searchCart: any
) => {
  const dispatch = useDispatch();
  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: data.amount,
      },
    ]);
    dispatch(clear(index));
  }

  return (
    <FoodBox key={data.id.toString()}>
      <InfoScreen id={data.id} desc={data.desc}></InfoScreen>
      <FoodPhoto theme={data.imageUrl}>
        {data.aviable ? null : (
          <>
            <OswaldText>{getString("home", "notAvailable")}</OswaldText>
            <div style={{ position: "absolute" }}>
              <BlockImg />
            </div>
          </>
        )}
        <FoodButtonsDiv>
          <FoodPhotoButton
            icon={"info"}
            isActive={data.aviable}
            size={`30px`}
            id={data.id}
          />
          <FoodPhotoButton icon={"cart"} onClick={() => add()} size={`30px`} />
        </FoodButtonsDiv>
      </FoodPhoto>
      <FoodBoxTitleCont>
        <FoodBoxText>{cutText(data.name, 32)}</FoodBoxText>
      </FoodBoxTitleCont>
      <FoodBoxRestConst>
        <SerifText style={{ flex: 1 }}>
          {data.price}$/{data.unit}
        </SerifText>
        {data.aviable ? (
          <FoodBoxAmountCont>
            <FoodPhotoButton
              icon={"minus"}
              sqr={true}
              size={`25px`}
              onClick={() =>
                data.amount > 1 ? dispatch(decrementAmount(index)) : null
              }
            />
            <SerifText>{data.amount}</SerifText>
            <FoodPhotoButton
              icon={"plus"}
              sqr={true}
              size={`25px`}
              onClick={() =>
                data.amount < 99 ? dispatch(incrementAmount(index)) : null
              }
            />
          </FoodBoxAmountCont>
        ) : null}
      </FoodBoxRestConst>
    </FoodBox>
  );
};
