    <FlexPart theme={1}>
      <div>
        <ListHandleBox
          onClick={() => setActive(!active)}
          theme={active}
        ></ListHandleBox>
        {active ? generateList(cart, setCart, "mode") : null}
      </div>
    </FlexPart>
  );
};
