import { SearchImg } from "../locales/images/search";
import { StartSearchImg } from "../locales/images/startSearch";
import { Filter } from "../models/Filter";
import { Flex } from "../style";
import { Button, IconButton, TxtButton } from "../styles/Buttons";
import { InfoScreenTextBox } from "../styles/MainScreen";
import {
  SearchInput,
  SearchHandleBox,
  SearchMainBox,
  SearchButtonBox,
  SearchCategoryBox,
  SearchSubMainBox,
} from "../styles/Search";
import { LinkTextBox, SerifTextS, SerifTextSS } from "../styles/TextStyles";
import { FilterCatButton } from "./buttons/FilterCatButton";
import { Filters } from "./Filters";
import { ChooseAmountOfProducts } from "./functions/chooseAmountOfProducts";
export const Search = () => {
  const [isHidden, setIsHidden] = useState(false);

  return (
    <SearchMainBox theme={isHidden}>
      <SearchSubMainBox>
        <SearchInput />
        <Filters filter={sortBy}></Filters>
      </SearchSubMainBox>
      <Flex>
        <SearchCategoryBox>
          {filters.map((data: any, index: any) => (
            <FilterCatButton data={data} allData={filters} />
          ))}
        </SearchCategoryBox>
        <ChooseAmountOfProducts numberOnSite={onPage} />
      </Flex>
      <SearchHandleBox onClick={() => setIsHidden(!isHidden)}>
        {isHidden ? <SearchImg /> : <SearchImg />}
      </SearchHandleBox>
    </SearchMainBox>
  );
};
