import styled from "styled-components";
import { rawColors, colors } from "./locales/colors";

export const FooterBox = styled.div`
  background-color: ${rawColors.pink};
  height: 50px;
  position: relative;
  position: relative;
`;

export const MainBodyBox = styled.div`
  background-color: ${colors.bg};
  min-height: 100%;
  margin-top: 50px;
  display: flex;
`;

export const AppBox = styled.div`
  height: 100%;
  width: 100%;
  background-color: ${colors.bg};
`;

export const FoodBox = styled.div`
  max-height: 400px;
  min-height: 200px;
  min-width: 200px;
  max-width: 400px;
  border: 2px solid red;
  border-radius: 30px;
  margin: 10px;
  display: flex;
  flex-direction: column;
`;

export const GridBox = styled.div`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

export const CartBox = styled.div`
  position: fixed;
  right: -17px;
  height: 90%;
  width: 20%;
  overflow: hidden;
  z-index: 0;
  background-image: url(${(props) => props.theme});
  background-repeat: no-repeat;
  background-size: 100% 100%;
  scroll-behaviour: smooth;
  overflow-y: scroll;
  box-sizing: content-box;
`;

export const CardSliderBox = styled.div`
  flex: 1;
  width: 100%;
  background-color: ${colors.bgOp60};
  margin-left: 100%;
  z-index: -4;
  align-items: center;
  display: flex;
  flex-direction: column;
`;

export const FoodPhoto = styled.img`
  height: 220px;
  width: 220px;
  border-radius: 28px 28px 0px 0px;
`;

export const HeaderBox = styled.div`
  background-color: ${colors.bg};
  height: 50px;
  position: fixed;
  width: 100%;
  top: 0;
  display: flex;
  justify-content: center;
  z-index: 2;
`;

export const LogoBox = styled.div`
  flex: 1;
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const TitleTxt = styled.div`
  flex: 1;
  background-color: ${colors.bg};
  z-index: 3;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const LoginBox = styled.div`
  flex: 1;
  z-index: 5;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const CartFoodElement = styled.div`
  height: 50px;
  width: 95%;
  display: flex;
  background: linear-gradient(90deg, rgba(107, 172, 0, 0.8) 0%, #f6f3f3 100%);
  margin: 10px 15px 0px 15px;
`;

export const SmallImg = styled.img`
  width: 44px;
  height: 44px;
  align-self: center;
  justify-content: center;
  display: flex;
`;

export const FlexPart = styled.div`
  display: flex;
  flex: ${(props) => props.theme};
  align-items: center;
  justify-content: space-evenly;
`;

export const ButtonPhoto = styled.button`
  width: ${(props) => props.theme.size};
  height: ${(props) => props.theme.size};
  background-color: ${(props) =>
    props.theme.isActive === false ? colors.buttonColor1 : colors.buttonbg};
  border-radius: ${(props) => (props.theme.sqr ? "0px" : "100000px")};
  border: 2px solid ${colors.buttonBorder};
  display: flex;
  justify-content: center;
  align-items: center;
  // &:hover {
  //   transition: opacity 0.3s;
  //   opacity: 0;
  // }
  // &:not(:hover) {,
  //   transition: opacity 0.3s;
  //   opacity: 1;
  // }
`;

export const CartDownBox = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 15px;
`;
