import React, { useState } from "react";
import { FoodBox, FoodPhoto } from "../styles/MainScreen";
import { AddSubButt } from "../styles/Buttons";
import { Text } from "../styles/TextStyles";
import { Food } from "./Food";
import { FoodPhotoButton } from "../components/buttons/FoodPhotoButtons";
import { namedColors } from "../locales/colors";

export const FoodTile = (data: Food, cart: Food[], setCart: any) => {
  const [amount, setAmount] = useState(1);

  function add() {
    setCart((oldArray: any) => [
      ...oldArray,
      {
        id: data.id,
        name: data.name,
        price: data.price,
        imageUrl: data.imageUrl,
        amount: amount,
      },
    ]);
    setAmount(1);
  }

  return (
    <FoodBox key={data.id.toString()}>
      <FoodPhoto alt="No img?" theme={data.imageUrl} />
      <div
        style={{
          display: "flex",
          flex: 1,
          justifyContent: "space-between",
          padding: "5px",
        }}
      >
        <FoodPhotoButton icon={"cart"} color={namedColors.buttonColor1} />
        <FoodPhotoButton icon={"info"} color={namedColors.buttonColor1} />
      </div>

      {/* <button onClick={() => add()}>Add to cart</button> */}
      {/* <div style={{ flexDirection: "row" }}>
        <AddSubButt onClick={() => (amount > 0 ? setAmount(amount - 1) : null)}>
          -
        </AddSubButt>
        <Text>{amount}</Text>
        <AddSubButt onClick={() => setAmount(amount + 1)}>+</AddSubButt>
      </div> */}
      {/* <Text>{data.name}</Text> */}
    </FoodBox>
  );
};
