import { Food } from "../../models/Food";

export function sortFoodBy(food: Food[], filters: any) {
  function sortBy() {
    switch (type) {
      case "price":
        return food.sort((a, b) =>
          !inOrder ? a.price - b.price : b.price - a.price
        );
      case "name":
        return food.sort(function (a, b) {
          if ((a.name < b.name && inOrder) || (b.name < a.name && !inOrder)) {
            return -1;
          }
          if ((a.name > b.name && inOrder) || (b.name > a.name && !inOrder)) {
            return 1;
          }
          return 0;
        });
      default:
        return food;
    }
  }

  const { type, inOrder } = filters.sortBy;
}
