import { InputsI } from "../../models/Inputs";
import { margin } from "../../styles/Constants";
import { InputOrderBox } from "../../styles/Input";
import { InputTitleText } from "../../styles/TextStyles";
import { InputBase, InputButton, InputPopup } from "../Input";

export function setInputs(
  data: InputsI,
  register: any,
  setPopup: any,
  getValues?: any,
  errors?: any
) {
  const Input = ({ item }: any) => {
    switch (item.type) {
      case "popup":
        return (
          <InputPopup
            register={register}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      case "text":
        return <InputBase register={register} data={item} errors={errors} />;
      case "button":
        return (
          <>
            <InputButton
              register={register}
              data={item}
              setOpenPopup={setPopup ? setPopup : null}
              getValues={getValues}
            />
          </>
        );
      default:
        return null;
    }
  };

  return data.data.map((item, index) => (
    <InputOrderBox>
      <InputTitleText>{item.title}</InputTitleText>
      <Input item={item} />
    </InputOrderBox>
  ));
}

export function setSingleInput(data: any, register: any, setPopup: any) {
  const Input = ({ item }: any) => {
    switch (item.type) {
      case "popup":
        return (
          <InputPopup
            register={register}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      case "text":
        return (
          <InputBase
            register={register}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      case "button":
        return (
          <InputButton
            register={register}
            data={item}
            setOpenPopup={setPopup ? setPopup : null}
          />
        );
      default:
        return null;
    }
  };
  return (
    <InputOrderBox theme={{ marginY: margin.l }}>
      <InputTitleText>{data.title}</InputTitleText>
      <Input item={data} />
    </InputOrderBox>
  );
}
