import { useState } from "react";
import { useForm } from "react-hook-form";
import {
  Constants,
  inputDataDelivery,
  inputDataPersonal,
  inputDataSpecial,
  loginData,
  passwordData,
} from "../../styles/Constants";
import { setInputs } from "../functions/setInputsToForm";
import { popupBlock } from "../PopupBlock";
import { GridBox2 } from "../../style";
import { FormBox, FormSpecialBox } from "../../styles/OrderTrans";
import { GrayButtonInput } from "../../styles/Input";
import {
  ButtonText,
  InputSectionText,
  SerifText,
} from "../../styles/TextStyles";
import { GrayButton } from "../../styles/Buttons";
import { LoginBox } from "../../styles/Header";

export const FormLogin = () => {
  const {
    register,
    setValue,
    handleSubmit,
    getValues,
    formState: { errors },
  } = useForm<FormData>();
  const onSubmit = (data: any) => console.log(getValues);
  const [popup, setPopup] = useState({ isActive: false, variant: "" });

  return (
    <LoginBox>
      <>{setInputs(loginData, register, setPopup, errors)}</>
    </LoginBox>
  );
};
