import styled from "styled-components";
import { rawColors, colors } from "../locales/colors";

export const Mainbox = styled.div`
  min-height: 100%;
  margin-top: 50px;
  display: flex;
`;

export const GridBox = styled.div`
  min-height: 100%;
  width: 80%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
`;

export const FoodBox = styled.div`
  height: 450px;
  min-width: 200px;
  max-width: 400px;
  border: 1.5px solid ${colors.foodBorder};
  border-radius: 30px;
  margin: 10px;
  display: flex;
  flex-direction: column;
`;

export const FoodPhoto = styled.div`
  height: 220px;
  width: 220px;
  border-radius: 28px 28px 0px 0px;
  background-image: url(${(props) => props.theme});
  background-size: 220px 220px;
  background-repeat: no-repeat;
  display: flex;
  align-items: flex-end;
`;

export const FoodButtonsDiv = styled.div`
  display: flex;
  flex-direciton: row;
  flex: 1;
  justify-content: space-between;
  padding-bottom: 2px;
`;
