import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { CategoryFilters } from "../locales/filters";
import { DeleteImg } from "../locales/images/delete";
import { SearchImg } from "../locales/images/search";
import { StartSearchImg } from "../locales/images/startSearch";
import { Filter } from "../models/Filter";
import { Flex } from "../style";
import { Button, IconButton, TxtButton } from "../styles/Buttons";
import { InfoScreenTextBox } from "../styles/MainScreen";
import {
  SearchInput,
  SearchHandleBox,
  SearchMainBox,
  SearchButtonBox,
  SearchCategoryBox,
  SearchSubMainBox,
} from "../styles/Search";
import { LinkTextBox, SerifTextS, SerifTextSS } from "../styles/TextStyles";
import { FilterCatButton } from "./buttons/FilterCatButton";
import { Filters } from "./Filters";
import { ChooseAmountOfProducts } from "./functions/chooseAmountOfProducts";
export const Search = () => {
  const [isHidden, setIsHidden] = useState(false);
  const search = useSelector((state: any) => state.search);
  const [sortBy, setSortBy] = useState(search.sortBy);
  const [filters, setFilters] = useState(search.filters);
  const [onPage, setOnPage] = useState(search.onPage);

  console.log(search);
  return (
    <SearchMainBox theme={isHidden}>
      <SearchSubMainBox>
        <SearchInput />
        <SearchButtonBox>
          <StartSearchImg data={[sortBy, filters, onPage]} />
        </SearchButtonBox>
        <Filters filter={sortBy} setSetting={setSortBy}></Filters>
      </SearchSubMainBox>
      <Flex>
        <SearchCategoryBox>
          {filters.map((data: any, index: any) => (
            <FilterCatButton
              data={data}
              allData={filters}
              setSetting={setFilters}
            />
          ))}
        </SearchCategoryBox>
        <ChooseAmountOfProducts numberOnSite={onPage} setSetting={setOnPage} />
      </Flex>
      <SearchHandleBox onClick={() => setIsHidden(!isHidden)}>
        {isHidden ? <SearchImg /> : <SearchImg />}
      </SearchHandleBox>
    </SearchMainBox>
  );
};
