import { useState } from "react";
import { InputBase } from "../styles/Input";
import { ErrorInputText } from "../styles/TextStyles";

export const Input = ({ props, filter, type, data, register }: any) => {
  const [value, setValue] = useState("");

  function checkInput(e: any) {
    if (e.match(filter) != null) {
      setValue(e);
    }
  }

  function getMessage(type: string) {
    switch (type) {
      case "email":
        return "SIKE ZŁY EMAIL";
      case "login":
        return "SIKE ZŁY LOGIN";
      case "password":
        return "SIKE ZŁE HASŁO";
      case "noValidation":
        return "SIKE NIE TAKIE HASŁO I LOGIN";
      default:
        return "cokolwiek";
    }
  }

  return (
    <InputBase
      theme={{ width: "100%" }}
      placeholder={data?.placeholder}
      {...register}
    ></InputBase>
  );
};
