import { useState } from "react";
import { InputBase } from "../styles/Input";
import { ErrorInputText } from "../styles/TextStyles";

export const Input = ({ props, filter, placeholder }: any) => {
  const [value, setValue] = useState("");

  function wariunek(e: any) {
    if (e.match(filter) != null) {
      setValue(e);
    }
  }

  function getMessage(type: string) {
    switch (type) {
      case "email":
        return "SIKE ZŁY EMAIL";
    }
  }

  return (
    <div>
      <InputBase
        {...props}
        value={value}
        onChange={(e) => wariunek(e.target.value)}
        placeholder={placeholder}
      ></InputBase>
      <ErrorInputText>{getMessage(props.type)}</ErrorInputText>
    </div>
  );
};
