import { useState } from "react";
import { InputBase } from "../styles/Input";

export const Input = ({ props, filter, placeholder }: any) => {
  const [value, setValue] = useState("");

  function wariunek(e: any) {
    if (e.match(filter) != null) {
      setValue(e);
    }
  }

  return (
    <>
      <InputBase
        {...props}
        value={value}
        onChange={(e) => wariunek(e.target.value)}
        placeholder={placeholder}
      ></InputBase>
      <span style={{ color: "red" }}>Error message</span>
    </>
  );
};
