import React, { useState } from "react";
import { FoodBox, FoodPhoto, FoodPhotoTitle } from "../style";
import { Food } from "./Food";

export function FoodTile(data: Food, cart: Food[], setCart: any) {
  const [amount, setAmount] = useState(1);

  function checkIfNoDupes() {
    console.log(cart.filter((check) => data.id === check.id));
  }

  return (
    <FoodBox>
      <FoodPhoto alt="No img?" src={data.imageUrl} />
      <div style={{ margin: "10px" }}>
        <button
          onClick={() =>
            setCart((oldArray: any) => [
              ...oldArray,
              {
                name: data.name,
                price: data.price,
                imageUrl: data.imageUrl,
                amount: amount,
              },
            ])
          }
        >
          Add to cart
        </button>
      </div>
      <div style={{ flexDirection: "row" }}>
        <button onClick={() => (amount > 0 ? setAmount(amount - 1) : null)}>
          -
        </button>
        {amount}
        <button onClick={() => setAmount(amount + 1)}>+</button>
      </div>
      <FoodPhotoTitle>{data.name}</FoodPhotoTitle>
    </FoodBox>
  );
}
