export function cutText(text: string, length: number) {
  console.log(`SIOKEEEEEEEEEEE`, text[length]);
  if (text.length < length) return text;
  else
    return (
      text.substr(0, text[length - 1] === " " ? length - 2 : length) + "..."
    );
}
