import { useState } from "react";
import { CategoryFIlters } from "../locales/filters";
import { DeleteImg } from "../locales/images/delete";
import { SearchImg } from "../locales/images/search";
import { StartSearchImg } from "../locales/images/startSearch";
import { Filter } from "../models/Filter";
import { Button, IconButton, TxtButton } from "../styles/Buttons";
import { InfoScreenTextBox } from "../styles/MainScreen";
import {
  SearchInput,
  SearchHandleBox,
  SearchMainBox,
  SearchButtonBox,
  SearchCategoryBox,
  SearchSubMainBox,
} from "../styles/Search";
import { LinkTextBox, SerifTextS, SerifTextSS } from "../styles/TextStyles";
import { filterCatButton } from "./buttons/FilterCatButton";
import { Filters } from "./Filters";
export const Search = ({ filter, setFilter }: any) => {
  const [isHidden, setIsHidden] = useState(false);
  const [catFilters, setCatFilters] = useState(CategoryFIlters);

  return (
    <SearchMainBox theme={isHidden}>
      <SearchSubMainBox>
        <SearchInput />
        <SearchButtonBox>
          <StartSearchImg />
        </SearchButtonBox>
        <Filters filter={filter} setFilter={setFilter}></Filters>
      </SearchSubMainBox>
      <div style={{ display: "flex" }}>
        <SearchCategoryBox>
          {catFilters.map((data, index) =>
            filterCatButton(data, setCatFilters, catFilters)
          )}
        </SearchCategoryBox>
        <div
          style={{
            width: "90px",
            height: "20px",
            flexDirection: "row",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <LinkTextBox>
            <TxtButton>30</TxtButton>|<TxtButton>60</TxtButton>|
            <TxtButton>90</TxtButton>
          </LinkTextBox>
        </div>
      </div>
      <SearchHandleBox onClick={() => setIsHidden(!isHidden)}>
        {isHidden ? <SearchImg /> : <SearchImg />}
      </SearchHandleBox>
    </SearchMainBox>
  );
};
