import { createSlice } from "@reduxjs/toolkit";

export interface FormI {
  data: {
    accessCode: string;
    city: string;
    companyName: string;
    deliveryTime: string;
    firstLastName: string;
    floor: string;
    email: string;
    flatNumber: string;
    houseNumber: string;
    payWith: string;
    phoneNumber: string;
    postcode: string;
    streetName: string;
    note: string;
    voutcher: string;
  };
}

const initialState: FormI = {
  data: {
    accessCode: "",
    city: "",
    companyName: "",
    deliveryTime: "",
    firstLastName: "",
    floor: "",
    email: "",
    flatNumber: "",
    houseNumber: "",
    payWith: "",
    phoneNumber: "",
    postcode: "",
    streetName: "",
    note: "",
    voutcher: "",
  },
};
export const formSlice = createSlice({
  name: "form",
  initialState,
  reducers: {
    setForm: (state, action) => {
      let newState = Object.assign({}, state.data);
      switch (action.payload.upgr) {
        case `accessCode`:
          break;
        case `city`:
          break;
        case `companyName`:
          break;
        case `deliveryTime`:
          break;
        case `firstLastName`:
          break;
        case `floor`:
          break;
        case `email`:
          break;
        case `flatNumber`:
          break;
        case `houseNumber`:
          break;
        case `payWith`:
          break;
        case `phoneNumber`:
          break;
        case `postcode`:
          break;
        case `streetName`:
          break;
        case `note`:
          break;
        case `voutcher`:
          break;
        default:
          break;
      }
    },
    setAccess: (state, action) => {
      state.data["accessCode"] = action.payload;
    },
    clearForm: (state) => {
      state.data = initialState.data;
    },
  },
});

export const { clearForm, setForm } = formSlice.actions;

export default formSlice.reducer;
