import { useState, useEffect } from "react";
import { Food } from "../../models/Food";
import {
  Constants,
  inputDataDelivery,
  InputDataI,
  inputDataPersonal,
  inputDataSpecial,
  InputsI,
} from "../../styles/Constants";
import {
  FormBox,
  FormSpecialBox,
  GridBox,
  SectionFormTitleBox,
  SummaryBox,
  TestListElementBox,
} from "../../styles/OrderTrans";
import {
  InputSectionText,
  InputTitleText,
  SerifText,
  SerifTextL,
} from "../../styles/TextStyles";
import { FoodData } from "../../testData/FoodData";
import { moveCartSlider } from "../functions/moveCartSlider";
import { priceAll } from "../functions/priceAll";
import { Input } from "../Input";
import { SummaryList } from "../SummaryList";

export const OrderTransactionSub = ({ data }: any) => {
  const [cart, setCart] = useState<Food[]>(data ? data : []);
  const [windowSize, setWindowSize] = useState(getWindowSize());
  function getWindowSize() {
    const { innerWidth, innerHeight } = window;
    return { innerWidth, innerHeight };
  }
  useEffect(() => {
    function handleWindowResize() {
      setWindowSize(getWindowSize());
    }

    window.addEventListener("resize", handleWindowResize);
    moveCartSlider(cart?.length);
    return () => {
      window.removeEventListener("resize", handleWindowResize);
    };
  }, [cart]);

  function setInputs(data: InputsI) {
    return data.data.map((item, index) => (
      <div
        style={{
          margin: "30px 10px 30px 10px",
        }}
      >
        <InputTitleText>{item.title}</InputTitleText>
        {getInputType(item)}
      </div>
    ));
  }

  const GetInputType = (data: any) => {
    switch (data.type) {
      default:
        return <Input data={data} />;
    }
  };

  return (
    <>
      <GridBox theme={{ width: windowSize.innerWidth }}>
        <InputSectionText>{Constants.forms.deliveryData}</InputSectionText>
        <FormBox>{setInputs(inputDataDelivery)}</FormBox>
        <InputSectionText>{Constants.forms.deliveryTime}</InputSectionText>
        <FormBox>{setInputs(inputDataPersonal)}</FormBox>
        <FormSpecialBox>{setInputs(inputDataSpecial)}</FormSpecialBox>
      </GridBox>
      <SummaryBox
        theme={{
          width: windowSize.innerWidth,
        }}
      >
        <SummaryList data={data} />
      </SummaryBox>
    </>
  );
};
