import { useEffect } from "react";
import { Food } from "../models/Food";
import { CardSlider, CartFoodElement } from "../style";
import { CartFood } from "./CartFood";

export const CartSliderBox = ({ cart, setCart }: any) => {
  function priceAll() {
    let x: number = 0;
    cart.map((data: any) => (x = x + data.price * data.amount));
    return x;
  }

  function generateList() {
    let clone = [...cart];
    if (cart.length >= 2) {
      let qqq = clone.findIndex(
        (data1) => data1.id === clone[clone.length - 1].id
      );

      if (qqq !== -1) {
        clone[qqq].amount = clone[qqq].amount + clone[cart.length - 1].amount;
        clone.slice(clone.length - 1);
      }
    }
    console.log(clone);
    return clone.map((element: Food, index: number) => (
      <CartFood data={element} cart={clone} setCart={setCart} index={index} />
    ));
  }

  return (
    <CardSlider id="CardSlider">
      {generateList()}
      Suma: {priceAll()}
    </CardSlider>
  );
};
