import styled from "styled-components";
import { colors, rawColors } from "../locales/colors";
import { Constants } from "./Constants";

export const Text = styled.label`
  font-size: 25px;
  color: ${rawColors.blue};
`;

export const MainTitleText = styled.label`
  font-size: 35px;
  color: ${rawColors.red};
`;

export const FoodBoxText = styled.section`
  width: 100%;
  overflow: hidden;
  font-family: "PT Serif Caption";
  font-size: 20px;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100;
  line-height: 24px;
  padding: 2px;
  text-align: center;
`;

export const SerifText = styled.section`
  font-family: "PT Serif Caption";
  font-size: 1.5em;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100;
  line-height: 24px;
  padding: 2px;
  user-select: none;
`;

export const SerifTextS = styled.section`
  font-family: "PT Serif Caption";
  font-size: 14px;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100px;
  line-height: 18px;
  user-select: none;
  text-align: center;
  width: 100%;
`;

export const SerifTextSS = styled.section`
  font-family: "PT Serif Caption";
  font-size: 12px;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100px;
  line-height: 18px;
  user-select: none;
  text-align: center;
`;

export const OswaldText = styled.section`
  font-family: Oswald;
  font-size: 37px;
  font-style: normal;
  font-variant: normal;
  font-weight: 700;
  line-height: 26.4px;
  color: red;
  text-shadow: -1px 0 black, 0 1px black, 1px 0 black, 0 -1px black;
  position: absolute;
  margin-top: 100px;
  text-align: center;
  user-select: none;
  width: ${Constants.size.foodPhotoSize};
`;

export const IrishText = styled.section`
  font-family: "Irish Grover";
  font-size: 38px;
  font-style: normal;
  font-variant: small-caps;
  font-weight: 100;
  line-height: 24px;
  padding: 2px;
  user-select: none;
`;
