export function popupBlock(isActive: boolean, windowSize: any) {
  if (isActive) {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "hidden";
    }
    return (
      <div
        style={{
          position: "absolute",
          top: 0,
          width: windowSize.innerWidth,
          height: windowSize.innerWidth,
          backgroundColor: "black",
          opacity: 0.3,
          zIndex: 1000,
        }}
      ></div>
    );
  } else {
    const html = document.querySelector("html");
    if (html) {
      html.style.overflow = "auto";
    }
  }
}
