import { Food } from "../../models/Food";

export function sortFoodBy(food: Food[], type: any, a: any, b: any) {
  switch (type) {
    case "amount":
      return food.sort(function ({ a, b }: any) {
        return a.aviable ? b.amount + a.amount : b.amount - a.amount;
      });
    case "price":
      return food.sort(function ({ a, b }: any) {
        return a.aviable ? b.price + a.price : b.price - a.price;
      });
    case "name":
      return food.sort(function ({ a, b }: any) {
        return a.aviable
          ? a.name.localeCompare(b.name)
          : b.name.localeCompare(a.name);
      });
    default:
      return food;
  }
}
