import { Food } from "../../models/Food";

export function sortFoodBy(food: Food[], type: any) {
  switch (type) {
    case "amount":
      return food.sort((a, b) => a.amount - b.amount);
    case "price":
      return food.sort((a, b) => a.price - b.price);
    case "name":
      return function (a, b) {
        return a - b;
      };
    default:
  }
}
