import { useEffect } from "react";
import { Food } from "../models/Food";
import { CardSlider, CartFoodElement } from "../style";
import { CartFood } from "./CartFood";

export const CartSliderBox = ({ cart }: any) => {
  function priceAll() {
    let x: number = 0;
    cart.map((data: any) => (x = x + data.price * data.amount));
    return x;
  }

  return (
    <CardSlider id="CardSlider">
      {cart.map((element: Food, index: number) => (
        <CartFood data={element} cart={cart} index={index} />
      ))}
      Suma: {priceAll()}
    </CardSlider>
  );
};
