  //     case "login":
  //       return "SIKE ZŁY LOGIN";
  //     case "password":
  //       return "SIKE ZŁE HASŁO";
  //     case "noValidation":
  //       return "SIKE NIE TAKIE HASŁO I LOGIN";
  //     default:
  //       return "cokolwiek";
  //   }
  // }
  switch (data.type) {
    case "popup":
      return (
        <InputChoose
          theme={{ width: "100%" }}
          placeholder={data?.placeholder}
          defaultValue={data?.defaultValue}
          {...register}
        >
          {value === "deliveryTime"
            ? countTime().map((item: any) => (
                <option value={item}>{item}</option>
              ))
            : paymentMethods.map((item: any) => (
                <option value={item}>{item}</option>
              ))}
        </InputChoose>
      );
    default:
      return (
        <InputBase
          theme={{ width: "100%", height: "80%" }}
          placeholder={data?.placeholder}
          {...register}
        />
      );
  }
};
