import { useEffect } from "react";
import { colors } from "../locales/colors";
import { pl } from "../locales/languages/pl";
import { Food } from "../models/Food";

import { GrayButton, LoginButton } from "../styles/Buttons";
import {
  CardSliderBox,
  CartMainBox,
  CartDownBox,
  CartDownButtonBox,
} from "../styles/CartBox";
import { ButtonText, OswaldText, SerifText } from "../styles/TextStyles";
import { CartFood } from "./CartFood";
import { useNavigate } from "react-router-dom";
import { priceAll } from "./functions/priceAll";

export const CartSlider = ({ cart, setCart }: any) => {
  const navigate = useNavigate();

  function generateList() {
    let clone = [...cart];
    let count = clone.filter((data) => data.id === clone[clone.length - 1].id);
    let qqq = clone.findIndex(
      (data1) => data1.id === clone[clone.length - 1].id
    );

    if (count.length >= 2) {
      clone[qqq].amount = clone[qqq].amount + clone[cart.length - 1].amount;
      cart.pop();
    }
    return clone.map((element: Food, index: number) => (
      <CartFood
        data={element}
        cart={clone}
        setCart={setCart}
        index={index}
        number={element.amount}
      />
    ));
  }

  return (
    <CardSliderBox id="CardSlider">
      <CartMainBox>{generateList()}</CartMainBox>
      {/* <CartDownBox>
        <SerifText>Suma: {priceAll(cart)}$</SerifText>
        <CartDownButtonBox>
          <GrayButton
            onClick={() =>
              navigate("/OrderTransaction", {
                state: {
                  cart,
                },
              })
            }
          >
            <ButtonText>{pl.order}</ButtonText>
          </GrayButton>
          <GrayButton onClick={() => setCart([])}>
            <ButtonText>{pl.clear}</ButtonText>
          </GrayButton>
        </CartDownButtonBox>
        <GrayButton>
          <ButtonText>{pl.saveList}</ButtonText>
        </GrayButton>
      </CartDownBox> */}
    </CardSliderBox>
  );
};
