import { useEffect, useState } from "react";
import { colors, rawColors } from "../locales/colors";
import { DeleteImg } from "../locales/images/delete";
import { Food } from "../models/Food";
import { CartFoodElement, FlexPart, SmallImg } from "../style";
import { TestListElementBox } from "../styles/OrderTrans";
import { SerifText, SerifTextS } from "../styles/TextStyles";
import { FoodPhotoButton } from "./buttons/FoodPhotoButtons";
import { cutText } from "./functions/cutText";

export const CartFood = ({ data, cart, index, number, setCart }: any) => {
  const [amount, setAmount] = useState<number>(data.amount);
  let value = data.price * number;

  function changeAmountPlus(amount: number) {
    if (number < 99) {
      setAmount(number + amount);
      let newData = [...cart];
      newData[index].amount = number + amount;
      setCart(newData);
    }
  }

  function changeAmountMinus(amount: number) {
    if (number <= 0) {
      setCart(cart.filter((item: any) => item !== data));
    } else {
      setAmount(number - amount);
      let newData = [...cart];
      newData[index].amount = number - amount;
      setCart(newData);
    }
  }

  function deleteFood() {
    setCart(cart.filter((item: any) => item !== data));
  }

  return (
    <TestListElementBox key={data.id.toString()}>
      <div>
        <div style={{ flex: 3, marginLeft: "10px" }}>
          <SerifTextS>{cutText(data.name, 15)}</SerifTextS>
        </div>
        <div style={{ flex: 1 }}>
          <SerifTextS>x{data.amount}</SerifTextS>
        </div>
        <div style={{ flex: 1 }}>
          <SerifTextS>{data.price * data.amount}$</SerifTextS>
        </div>
      </div>

      <FlexPart theme={3}>
        <FoodPhotoButton
          icon={"delete"}
          sqr={true}
          onClick={() => deleteFood()}
          size={`24px`}
        />
        <FoodPhotoButton
          icon={"minus"}
          sqr={true}
          onClick={() => changeAmountMinus(1)}
          size={`24px`}
        />
        <FoodPhotoButton
          icon={"plus"}
          sqr={true}
          onClick={() => changeAmountPlus(1)}
          size={`24px`}
        />
      </FlexPart>
    </TestListElementBox>
  );
};
