import { useState } from "react";
import { useForm } from "react-hook-form";
import {
  Constants,
  inputDataDelivery,
  inputDataPersonal,
  inputDataSpecial,
  loginData,
  newListData,
} from "../../styles/Constants";
import { setInputs, setSingleInput } from "../functions/setInputsToForm";
import { Flex, GridBox2 } from "../../style";
import { FormBox, FormSpecialBox } from "../../styles/OrderTrans";
import { GrayButtonInput, GrayButtonLogin } from "../../styles/Input";
import {
  ButtonText,
  InputSectionText,
  SerifText,
} from "../../styles/TextStyles";
import { GrayButton, GrayButtonPopup } from "../../styles/Buttons";
import { LoginBox } from "../../styles/Header";
import { getString } from "../../locales/languages/string";
import { useDispatch } from "react-redux";
import { closePopup, setPopup } from "../../store/popup";
import { addMyLists } from "../../store/lists";
import { List1 } from "../../testData/FoodData";

export const FormInput = (props: any) => {
  const {
    register,
    setValue,
    handleSubmit,
    getValues,
    formState: { errors },
  } = useForm<FormData>();
  const onSubmit = (data: any) => console.log(getValues());
  const { cart } = useSelector((state: any) => state.cart);

  const dispatch = useDispatch();
  function onConfirm() {
    if (props?.popup.onConfirm === "AddMyList") {
      dispatch(addMyLists([{ food: cart, title: "xD" }]));
      handleSubmit(onSubmit);
    } else {
      props?.popup.onConfirm();
    }
    dispatch(closePopup());
  }

  return (
    <div style={{ width: "80%", marginLeft: "10%" }}>
      <>{setSingleInput(newListData.data[0], register)}</>
      <Flex>
        <GrayButtonPopup onClick={() => dispatch(closePopup())}>
          {getString("buttons", "goBack")}
        </GrayButtonPopup>
        <GrayButtonPopup onClick={() => onConfirm()}>
          {getString("buttons", "save")}
        </GrayButtonPopup>
      </Flex>
    </div>
  );
};
